import { CELL_SIZE, Position } from './utils/utils';

interface GameBoardProps {
    snake: Position[];
    food: Position;
}

const SnakeBoard: React.FC<GameBoardProps> = ({snake, food}) => {
    const renderBoard = () => {
        let rows = [];
        for (let row = 0; row < CELL_SIZE; row++) {
            let cells = [];
            for (let col = 0; col < CELL_SIZE; col++) {
                let isSnake = snake.some((s) => s.x === col && s.y === row);
                let isFood = food.x === col && food.y === row;

                let style = {
                    backgroundColor: isSnake ? 'green' : isFood ? 'red' : 'white'
                };

                if (isSnake) {
                    cells.push(<div key={`${col}-${row}`} className="snake"></div>);
                }
                else if (isFood) {
                    cells.push(<div key={`${col}-${row}`} style={style}  className="food"></div>);
                }
                else {
                    cells.push(<div key={`${col}-${row}`} style={style}  className="boardBlock"></div>);
                }

            }
            rows.push(<div key={row} className="rows" >{cells}</div>);
        }
        return rows;
    }
    return (
        <>
            <div id="board" style={{'--cell-size': `${CELL_SIZE*40}px`} as React.CSSProperties}>
                {renderBoard()}
            </div>
        </>
    );
};

export default SnakeBoard;
