import { Direction } from 'readline';
import { CELL_SIZE, Position } from './utils/utils';


interface GameBoardProps {
    snake: Position[];
    food: Position;
    direction: Direction;
}

const SnakeBoard: React.FC<GameBoardProps> = ({snake, food, direction}) => {
    const renderBoard = () => {
        let rows = [];
        for (let row = 0; row < CELL_SIZE; row++) {
            let cells = [];
            for (let col = 0; col < CELL_SIZE; col++) {
                let isSnake = snake.some((s) => s.x === col && s.y === row);
                let isFood = food.x === col && food.y === row;
                let isSnakeHead = snake[0].x === col && snake[0].y === row;

                if (isSnake) {
                    if (isSnakeHead) {
                        cells.push(<div key={`${col}-${row}`} className="snakeHead snake boardBlock"></div>);
                    }
                    else {
                        cells.push(<div key={`${col}-${row}`} className="snake boardBlock"></div>);
                    }
                }
                else if (isFood) {
                    cells.push(<div key={`${col}-${row}`}  className="food boardBlock"></div>);
                }
                else {
                    cells.push(<div key={`${col}-${row}`} className="boardBlock"></div>);
                }

            }
            rows.push(<div key={row} className="rows" >{cells}</div>);
        }
        return rows;
    }
    return (
        <div>
            <div id="board" style={{'--cell-size': `${CELL_SIZE*40}px`} as React.CSSProperties}>
                {renderBoard()}
            </div>
        </div>
    );
};

export default SnakeBoard;
