import { useState, useEffect } from 'react';
import { useKey } from 'react-use';
import SnakeBoard from './SnakeBoard';
import { 
    CELL_SIZE, 
    generateFoodPosition, 
    initialSnake, 
    moveSnake, 
    Direction, 
    Position,
    gameover
} from './utils/utils';
import './SnakeGame.css'

const SnakeGame = () => {
    const [snake, setSnake] = useState<Position[]>(initialSnake);
    const [direction, setDirection] = useState<Direction>('RIGHT');
    const [food, setFood] = useState<Position>(generateFoodPosition);
    const [gameoverFlag, setGameOver] = useState(gameover)

    useEffect(() => {
        const interval = setInterval(() => moveSnake(direction, snake, setSnake, setFood, setGameOver), 100);
        return () => clearInterval(interval);
    }, [direction, snake]);

    useKey('ArrowUp', () => setDirection('UP'));
    useKey('ArrowDown', () => setDirection('DOWN'));
    useKey('ArrowLeft', () => setDirection('LEFT'));
    useKey('ArrowRight', () => setDirection('RIGHT'));


    return (
        <div
            id="moveSnake"
            tabIndex={0}
        >
            <SnakeBoard snake={snake} food={food} />
        </div>
    );
};


export default SnakeGame;
