// export const gameover = false;

export const CELL_SIZE = 20;

export interface Position {
    x: number,
    y: number
}
export type Direction = 'UP' | 'DOWN' | 'LEFT' | 'RIGHT';

export const initialSnake: Position[] = [{x: 5, y: 5 }];

export const generateFoodPosition = (): Position => {
    return {
        x: Math.floor(Math.random() * CELL_SIZE),
        y: Math.floor(Math.random() * CELL_SIZE)
    };
};


export const moveSnake = (
    direction: Direction,
    snake: Position[],
    setSnake: React.Dispatch<React.SetStateAction<Position[]>>,
    setFood: React.Dispatch<React.SetStateAction<Position>>,
    setGameOver: React.Dispatch<React.SetStateAction<boolean>>
  ) => {
    let newSnake = [...snake];
    let head = { ...newSnake[0] };
  
    switch (direction) {
      case 'UP':
        head.y -= 1;
        break;
      case 'DOWN':
        head.y += 1;
        break;
      case 'LEFT':
        head.x -= 1;
        break;
      case 'RIGHT':
        head.x += 1;
        break;
      default:
        break;
    }

    if (head.x < 0 || head.y < 0 || head.x >= CELL_SIZE || head.y >= CELL_SIZE) {
        setSnake(initialSnake);
        setGameOver(true);
        return ;
    }

    if (head.x === snake[0].x && head.y === snake[0].y) {
        setFood(generateFoodPosition());
    } else {
        newSnake.pop();
    }

    newSnake.unshift(head);
    setSnake(newSnake);

};
