import { useState, useEffect } from 'react';
import { useKey } from 'react-use';
import SnakeBoard from './SnakeBoard';
import { 
    CELL_SIZE, 
    generateFoodPosition, 
    initialSnake, 
    moveSnake, 
    Direction, 
    Position
} from './utils/utils';
import './css/SnakeGame.css'
import GameOver from './GameOver';

const SnakeGame = () => {
    const [snake, setSnake] = useState<Position[]>(initialSnake);
    const [direction, setDirection] = useState<Direction>('STOP');
    const [food, setFood] = useState<Position>(generateFoodPosition);
    const [gameoverFlag, setGameOver] = useState<boolean>(false)
    const [intervalTime, setIntervalTime] = useState<number>(200);

    useEffect(() => {
        const interval = setInterval(() => moveSnake(
            direction, 
            snake,
            food,
            intervalTime,
            setSnake, 
            setFood, 
            setGameOver,
            setIntervalTime,
            setDirection
        ), intervalTime);
        
        return () => clearInterval(interval);
    }, [direction, snake]);


    const handleKeyPress = (newDirection: Direction) => {
        if (
          (direction === 'UP' && newDirection === 'DOWN') ||
          (direction === 'DOWN' && newDirection === 'UP') ||
          (direction === 'LEFT' && newDirection === 'RIGHT') ||
          (direction === 'RIGHT' && newDirection === 'LEFT')
        ) {
          // Ignore the key press if it's the opposite direction
          return;
          console.log(newDirection);
        }
        setDirection(newDirection);
    };
    
    useKey('ArrowUp',    () => handleKeyPress('UP'));
    useKey('ArrowDown',  () => handleKeyPress('DOWN'));
    useKey('ArrowLeft',  () => handleKeyPress('LEFT'));
    useKey('ArrowRight', () => handleKeyPress('RIGHT'));
    // useKey('ArrowUp',    () => setDirection('UP'));
    // useKey('ArrowDown',  () => setDirection('DOWN'));
    // useKey('ArrowLeft',  () => setDirection('LEFT'));
    // useKey('ArrowRight', () => setDirection('RIGHT'));
  


    return (
        <div
            id="moveSnake"
            tabIndex={0}
        >
            { !gameoverFlag ?
                <SnakeBoard snake={snake} food={food} /> :
                <GameOver setGameOver={setGameOver} />
            }

        </div>
    );
};


export default SnakeGame;
