import { useEffect, useState } from 'react';
import StartMenu from "./StartMenu";
import TwoZeroFourEight from './2048/TwoZeroFourEight';
import SnakeGame from './SnakeGame/SnakeGame';
import GameOver from './GameOver';

const Game = () => {

    const [game, setGame] = useState<string>("menu");
    // const [start, setStart] = useState<boolean>(false);

    const selectGame = (game: string) => {
        // console.log(game);
        switch(game) {
            case "snake":
                return <SnakeGame setGame={setGame}/>;
            case "2048":
                return <TwoZeroFourEight setGame={setGame} />
                

            case "gameover":
                return <GameOver setGame={setGame} />
            case "menu":
                return  <StartMenu setGame={setGame} />;
            default:
                return <StartMenu setGame={setGame} />;


        }
    }

    return (
        <>
            {selectGame(game)}
        </>
    );

};

export default Game;
