import { useState, useEffect } from 'react';
import { useKey } from 'react-use';
import { CELL_SIZE, Position } from './utils/utils';

interface GameBoardProps {
    snake: Position[];
    food: Position;
}

const SnakeBoard: React.FC<GameBoardProps> = ({snake, food}) => {
    const renderBoard = () => {
        let rows = [];
        for (let row = 0; row < CELL_SIZE; row++) {
            let cells = [];
            for (let col = 0; col < CELL_SIZE; col++) {
                let isSnake = snake.some((s) => s.x === col && s.y === row);
                let isFood = food.x === col && food.y === row;
                let style = {
                    backgroundColor: isSnake ? 'green' : isFood ? 'red' : 'white'
                };
                cells.push(<div key={`${col}-${row}`} id="board" style={style}></div>);

            }
            rows.push(<div key={row} style={{ display: 'flex' }}>{cells}</div>)
        }
    }
    return (
        <>
        </>
    );
};

export default SnakeBoard;
