import StartMenu from "./StartMenu";
import Game2048 from './2048/Game2048';
import SnakeGame from './SnakeGame/SnakeGame';

const Game = () => {

    const [game, setGame] = useState<string>("");
    // const [start, setStart] = useState<boolean>(false);

    const selectGame = (game: string) => {
        console.log(game);
        switch(game) {
            case "snake":
                return <SnakeGame />;
                
            default:
                return  <StartMenu 
                            setGame={setGame} 
                            // selectGame={() => selectGame}
                        />


        }
    }

    // useEffect(() => {
    //     selectGame(game);
    // }, [game])

    return (
        <>
            
            {/* <StartMenu setGame={() => setGame} selectGame={() => selectGame(game)}/> */}
            {selectGame(game)}

            {/* { !start ?
                <StartMenu setStart={setStart} />  :
                <SnakeGame />
            } */}
        </>
    );

};

export default Game;
