import { useEffect, useState } from 'react';
import StartMenu from "./StartMenu";
import TwoZeroFourEight from './2048/TwoZeroFourEight';
import SnakeGame from './SnakeGame/SnakeGame';
import Name from './Name';
import axios from 'axios';
import { response } from 'express';

import { Score } from './SnakeGame/utils/utils';

import './css/Game.css';


const Game = () => {
    const [game, setGame] = useState<string>("name");
    const [scores, setScores] = useState<Score[]>([]);
    const [userName, setUserName] = useState<string>('名無し');
    const [userScore, setUserScore] = useState<number>(Number);


  useEffect(() => {
    /* 非同期関数 */
    const fetchScores = async () => {
      try {
        /* サーバーにHTTP GETリクエストを送信してスコアのリストを取得 */
        const response = await axios.get<Score[]>('http://localhost:3001/scores');
        setScores(response.data);
        console.log(scores);
      }
      catch (err) {
        console.error("Error fetching scores:", err);
      }
    };
    fetchScores();
  }, []);

  const handleDeleteScore = async () => {
    try {
      await axios.delete('http://localhost:3001/scores');
    
      const response = await axios.get<Score[]>('http://localhost:3001/scores');
      setScores(response.data);
    }
    catch (err) {
      console.log("Error submitting score:", err);
    }
  };

  
    const selectGame = (game: string) => {
        // console.log(game);
        switch(game) {
            case "snake":
                return  <SnakeGame 
                            setGame={setGame} 
                            setScores={setScores}
                            setUserScore={setUserScore} 
                            userScore={userScore}
                            userName={userName}
                        />;

            case "2048":
                return  <TwoZeroFourEight setGame={setGame} />

            case "name":
                return  <Name 
                          userName={userName} 
                          setUserName={setUserName} 
                          setGame={setGame} 
                        />
            case "menu":
                return  <StartMenu setGame={setGame} />;

            default:
                return  <StartMenu setGame={setGame} />;


        }
    }

    return (
        <>  
            <header>
              <div className="title">
                  <h1><a onClick={() => {setGame("menu"); setUserScore(0); }} >ゲーム集</a></h1>
              </div>
            </header>

            {selectGame(game)}  
            <button onClick={handleDeleteScore}>ランキング削除</button>
            <footer>
                <button onClick={() => {setGame("name"); setUserScore(0);}}>名前変更</button>
                <h2>High Scores</h2>
                <div id="rank">
                    <ul className='ranking'>
                        { scores.length > 0 ? scores.map((score) => (
                            <li key={score.id}>
                                <span className="name">{score.name === "" ? "名無し" : score.name}</span> : {score.score}点
                            </li>
                        )) :
                        <li className="empty">記録無し</li>}
                    </ul>
                </div>
            </footer>
        </>
    );

};

export default Game;
