import './css/StartMenu.css';
import SnakeGameImage from '../../public/snakeGame.png'

interface StartProps {
    setGame: React.Dispatch<React.SetStateAction<string>>;
}

const StartMenu = (props : StartProps) => {

    const gameStart = (value: string) => {
        props.setGame(value);
        // props.selectGame(game);
        // props.setStart({start: true});
    };

    return (
        <>
            <div id="startMenu">
                <h1>GameMenu</h1>
                <div className="game-label">
                    <img src="../../public/snakeGame.png" alt="Snake Game Icon" />
                    <span>スネークゲーム</span>
                    <button value="snake" onClick={() => gameStart("snake")} >Start</button>
                </div>

                <label>2048{' '}<button value="2048" onClick={() => gameStart("2048")}>Start</button></label>
            </div>
        </>
    );
};

export default StartMenu;
