import { useEffect, useState } from 'react';
import StartMenu from "./StartMenu";
import TwoZeroFourEight from './2048/TwoZeroFourEight';
import SnakeGame from './SnakeGame/SnakeGame';
import Name from './Name';
import axios from 'axios';
import { response } from 'express';

interface Score {
    id: number;
    name: string;
    score: number;
    created_at: string;
    ranking: number;
  }

const Game = () => {
    const [game, setGame] = useState<string>("name");
    const [scores, setScores] = useState<Score[]>([]);
    const [userName, setUserName] = useState<string>('名無し');
    const [userScore, setUserScore] = useState<number>(Number);


  useEffect(() => {
    /* 非同期関数 */
    const fetchScores = async () => {
      try {
        /* サーバーにHTTP GETリクエストを送信してスコアのリストを取得 */
        const response = await axios.get<Score[]>('http://localhost:3001/scores');
        setScores(response.data);
        console.log(scores);
      }
      catch (err) {
        console.error("Error fetching scores:", err);
      }
    };
    fetchScores();
  }, []);

  const handleScoreSubmit = async () => {
    try {
      /* スコアをサーバーに送信 -> 新しいスコアをデータベースに追加 */
      await axios.post('http://localhost:3001/scores', { name: userName, score: userScore });
      
      /* フォームの入力をクリア */
      // setUserName('名無し');
      setUserScore(Number);
      
      /* 最新のスコアリストを再取得して画面に反映 */
      const response = await axios.get<Score[]>('http://localhost:3001/scores');
      setScores(response.data);
    }
    catch (err) {
      console.log("Error submitting score:", err);
    }
  };

  const handleDeleteScore = async () => {
    try {
      await axios.post('http://localhost:3001/scores', { name: userName, score: userScore });
    
      const response = await axios.get<Score[]>('http://localhost:3001/scores');
      setScores(response.data);
    }
    catch (err) {
      console.log("Error submitting score:", err);
    }
  };

  
    const selectGame = (game: string) => {
        // console.log(game);
        switch(game) {
            case "snake":
                return  <SnakeGame 
                            setGame={setGame} 
                            setScores={setScores}
                            setUserScore={setUserScore} 
                            userScore={userScore}
                        />;

            case "2048":
                return  <TwoZeroFourEight setGame={setGame} />

            case "name":
                return  <Name 
                          userName={userName} 
                          setUserName={setUserName} 
                          setGame={setGame} 
                        />
            case "menu":
                return  <StartMenu setGame={setGame} />;

            default:
                return  <StartMenu setGame={setGame} />;


        }
    }

    return (
        <>  
            <header>
              <div className="title">
                  <h1><a onClick={() => setGame("menu")} >ゲーム集</a></h1>
              </div>
              {selectGame(game)}
            </header>            
            {/* <input
                type="number"
                value={userScore}
                onChange={(e) => setUserScore(Number(e.target.value))}
                placeholder='スコアの入力'
            /> 
            <button onClick={handleScoreSubmit}>スコアを送信</button>
            <br /> */}
            <footer>
              <button onClick={() => setGame("name")}>名前変更</button>
              <h2>High Scores</h2>
              <ul>
                  { scores.length > 0 ? scores.map((score) => (
                      <li key={score.id}>
                      {score.ranking}位 {score.name} : {score.score}点
                      </li>
                  )) :
                  <li>記録無し</li>}
              </ul>
            </footer>
        </>
    );

};

export default Game;
