import React, { useState, useEffect } from 'react';
import Game from './components/Game';
import './App.css';
import axios from 'axios';
import { response } from 'express';


interface Score {
  id: number;
  name: string;
  score: number;
  created_at: string;
  rank: number;
}

function App() {
  const [socres, setScores] = useState<Score[]>([]);
  const [userName, setUserName] = useState<string>('');
  const [userScore, setUserScore] = useState<number>(0);


  useEffect(() => {
    /* 非同期関数 */
    // const fetchScores = async () => {
    //   try {
    //     /* サーバーにHTTP GETリクエストを送信してスコアのリストを取得 */
    //     const response = await axios.get<Score[]>('http://localhost:3001/socres');
    //     setScores(response.data);
    //   }
    //   catch (err) {
    //     console.error("Error fetching scores:", err);
    //   }
    // };

    axios.get<Score[]>('htto://localhost:3001/scores')
      .then(response => {
        setScores(response.data);
      })
      .catch(err => {
        console.error("Error fetching scores:", err);
      })

    // fetchScores();
  }, []);

  const handleScoreSubmit = async () => {
    try {
      /* スコアをサーバーに送信 -> 新しいスコアをデータベースに追加 */
      await axios.post('http://localhost:3001/scores', { name: userName, score: userScore });
      
      /* フォームの入力をクリア */
      setUserName('');
      setUserScore(0);
      
      /* 細心のスコアリストを再取得して画面に反映 */
      const response = await axios.get<Score[]>('http://localhost:3001/scores');
      setScores(response.data);
    }
    catch (err) {
      console.log("Error submitting score:", err);
    }
  };



  return (
    <div>
      <h2>名前入力</h2>
        <div>
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            placeholder='名前を入力'
          />
          <input
            type="number"
            value={userScore}
            onChange={(e) => setUserScore(Number(e.target.value))}
            placeholder='スコアの入力'
          />
          <button onClick={handleScoreSubmit}>スコアを送信</button>
        </div>
      <Game />
    </div>
  );
}

export default App;
